import React, { useEffect, useState } from "react";
import "./food.css";
import { useMediaQuery } from "react-responsive";
import axios from "../axiosURL.js";
import { useDispatch, useSelector } from "react-redux";
import { createFood, loadFoods } from "../Reducer/foodReducer.js";

const Food = () => {
    const isTabletOrMobile = useMediaQuery({query : '(max-width: 800px)'});

    const foods = useSelector((state) => {return state.food})

    console.log(foods)

    useEffect(() => {
        dispatch(loadFoods());
    }, [])


    const dispatch = useDispatch();

    const attributeTranslation = {
        name: "Name",
        baseAmount: "Basismenge",
        energy: "Kalorien",
        fat: "Fett",
        carbohydrates: "Kohlenhydrate",
        protein: "Protein",
        salt: "Salz",
        fiber: "Balaststoffe",
        drink: "Getränk"
    }

    const measurements = {
        baseAmount: "g",
        energy: "kcal",
        fat: "g",
        carbohydrates: "g",
        protein: "g",
        salt: "g",
        fiber: "g",
    }

    const food = [
      {"name": "Apple", "baseAmount": 1, "energy": 52, "fat": 0.2, "carbohydrates": 14, "protein": 0.3, "salt": 0, "fiber": 2.4, "drink": false},
      {"name": "Potatoes", "baseAmount": 100, "energy": 77, "fat": 0.1, "carbohydrates": 17, "protein": 2, "salt": 0, "fiber": 2.2, "drink": false},
      {"name": "Rice", "baseAmount": 100, "energy": 130, "fat": 0.3, "carbohydrates": 28, "protein": 2.7, "salt": 0, "fiber": 0.4, "drink": false},
      {"name": "Salmon", "baseAmount": 100, "energy": 206, "fat": 13.4, "carbohydrates": 0, "protein": 22, "salt": 0.05, "fiber": 0, "drink": false},
      {"name": "Chicken", "baseAmount": 100, "energy": 165, "fat": 9.3, "carbohydrates": 0, "protein": 19, "salt": 0.1, "fiber": 0, "drink": false},
      {"name": "Chicken", "baseAmount": 100, "energy": 165, "fat": 9.3, "carbohydrates": 0, "protein": 19, "salt": 0.1, "fiber": 0, "drink": false},
      {"name": "Chicken", "baseAmount": 100, "energy": 165, "fat": 9.3, "carbohydrates": 0, "protein": 19, "salt": 0.1, "fiber": 0, "drink": false},
      {"name": "Chicken", "baseAmount": 100, "energy": 165, "fat": 9.3, "carbohydrates": 0, "protein": 19, "salt": 0.1, "fiber": 0, "drink": false}
    ]

    const newFood = { "name": "", "baseAmount": 0, "energy": 0, "fat": 0, "carbohydrates": 0, "protein": 0, "salt": 0, "fiber": 0, "drink": false };


    const [selectedFood, setSelectedFood] = useState();

    const handleFoodClick = (index) => {
        // Toggle selection when a row is clicked
        setSelectedFood(prevSelectedFood => (
            prevSelectedFood === foods[index] ? null : foods[index]
        ));
    };

    const handleAttributeChange = (attribute, value) => {
        // Update the selected food's attribute when an input field is changed
        setSelectedFood((prevSelectedFood) => ({
            ...prevSelectedFood,
            [attribute]: value
        }));

        console.log("Selected Food", selectedFood)
    };

    const handleNewFood = () => {
        setSelectedFood(newFood);
    }

    const handleSubmit = () => {
        axios.post("/fitness/food", selectedFood, {withCredentials:true})
        .then((res) => {
            console.log(res)
        }).catch((err) => {
            console.log(err)
        })
    }

    return (
        <div className={isTabletOrMobile ? ("Food__Container__Mobile") : ("Food__Container__Desktop")}>
            <div className="Food__Table__Button__Container">
            <button className="Food__AddButton" onClick={handleNewFood}>Neues Essen hinzufügen</button>
            <div className="Food__Table__Container">
                {foods.map((item, index) => (
                    <div key={index} onClick={() => handleFoodClick(index)} className={`Food__TableRow ${selectedFood.name === item.name ? "selected" : ""}`}>
                        {item.name}
                    </div>
                ))}
            </div>
            </div>
            <div className="Food__SelectedFood__Container">
                <form>
                <div className="Food__SelectedFood__Attributes">
                        {Object.keys(selectedFood).map((attribute, index) => (
                            <div key={index}>
                                <strong>{attributeTranslation[attribute]}: </strong>
                                <input
                                    type="text"
                                    value={selectedFood[attribute]}
                                    onChange={(e) => handleAttributeChange(attribute, e.target.value)}
                                />
                                <span> {measurements[attribute]}</span>
                            </div>
                        ))}
                </div>
                </form>
            <button onClick={handleSubmit} className="Food__SaveButton">Speichern</button>
            <button className="Food__DeleteButton">Löschen</button>
            </div>
        </div>
    );
};

export default Food;
