import { useMediaQuery } from 'react-responsive'
import './exerciseTable.css'
import React from 'react'

const ExerciseTable = ({ exercise, allExercises}) => {
    const isTabletOrMobile = useMediaQuery({query: '(max-width:800px)'})   

    return(
        <>
           <div className={isTabletOrMobile ? ("ExerciseTable__Container__Mobile") : ("ExerciseTable__Container__Desktop")}>
                <h3>Heutige Trainings</h3>
                <table className='ExerciseTable__Table'>
                    <tbody>
                        {exercise.map((item, index) => (
                            <tr key={index}>
                                <td style={{width: '60%'}}>{allExercises.find((exercise) => exercise._id === item.exerciseId)?.name}</td>
                                <td style={{width: '40%'}}>{item.timeInMinutes}min</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    )
}

export default ExerciseTable;
