import React, { useEffect, useState } from "react";
import { useMediaQuery } from "react-responsive";
import './fitnesscontainer.css'
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import axios from "axios";
import CaloriesOverview from "../CaloriesOverview/caloriesOverview";
import MacrosOverview from "../MacrosOverview/macrosOverview";
import FoodTable from "../FoodTable/foodTable";
import ExerciseTable from "../ExerciseTable/exerciseTable";

const Fitnesscontainer = () => {
    const isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px' })

    const [showCalendar, setShowCalendar] = useState(false)
    const [date, setDate] = useState(new Date())
    const [allProfiles, setAllProfiles] = useState([])
    const [profile, setProfile] = useState('')
    const [dayData, setDayDate] = useState('')


    useEffect(() => {
        getDayData(date, profile)
    }, [])

   const getDayData = (date, profile) => {
        axios.get("mocks/dayData.json").then((res) => {
            let day = res.data.find((day) => {
                return day.profileId===profile
            })
            setDayDate(day)
        }).catch((err) => {
            console.log(err)
        }) 
    }


    useEffect(() => {
        axios.get("/mocks/profileData.json").then((res) => {
            setAllProfiles(res.data)
            setProfile(res.data[0].id)
        });
    }, [])

    const formattedDay = new Intl.DateTimeFormat('de-DE', { weekday: 'short' }).format(date)
    const formattedDate = date.toLocaleDateString('de-DE')

    const handleChangeDate = (value) => {
        setDate(value)
        setShowCalendar(false)
    }

    const profileOptions = allProfiles.map((v) => {
        return (
            <option key={v.id} value={v.id}>{v.name}</option>
        )
    })

    const onProfileChange = (e) => {
        setProfile(e.target.value)
    }

    return(
        <div class='flex flex-col'>
            <div class='m-1 w-max h-14 bg-sky-300'>
                Daniel Brezer | 72kg
            </div>
        </div>


    )
}

export default Fitnesscontainer;
