import axios from "axios";
import React, { useEffect, useState } from "react";
import { useMediaQuery } from "react-responsive";
import './caloriesOverview.css'

const CaloriesOverview = ({food, allFoods, exercise, allExercises}) => {
    const isTabletOrMobile = useMediaQuery({ query : '(max-width: 800px' })

    const calculateTakenCalories = (food, allFoods) => {
        let calories = 0;

        food.forEach((item) => {
            const foodItem = allFoods.find((food) => food._id === item.foodId);
            if (foodItem) {
                amountDiff = item.amount / foodItem.baseAmount
                calories += foodItem.energy * amountDiff;
            }
        });

        return calories;
    };

    const [takenCalories, setTakenCalories] = useState(1500)

    const calorieDeficit = takenCalories - 0
    return(
        <>
            <div className={isTabletOrMobile ? ("CaloriesOverview__Container__Mobile") : ("CaloriesOverview__Container__Desktop")}>
                <p>Konsumierte Kalorien: {calculateTakenCalories} kcal</p>
                <p>Verbrannte Kalorien: {takenCalories} kcal</p>
                <p>Kaloriendefizit: {calorieDeficit} kcal</p>
            </div>
        </>
    )
}

export default CaloriesOverview;
