import { useMediaQuery } from 'react-responsive'
import './foodTable.css'
import React from 'react'

const FoodTable = ({allFoods, food}) => {

    console.log("Foodtable", food)
    const isTabletOrMobile = useMediaQuery({query: '(max-width:800px)'})    

    return(
        <>
                <div className={isTabletOrMobile ? ("FoodTable__Container__Mobile") : ("FoodTable__Container__Desktop") }>
                    <h3>Heutige Mahlzeiten</h3>
                    <table className='FoodTable__Table'>
                        <tbody>
                            {food.map((item, index) => (
                                <tr key={index}>
                                    <td style={{width: '60%'}}>{allFoods.find((food) => food._id === item.foodId)?.name}</td>
                                    <td style={{width: '40%'}}>{item.amount}g</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>               
                </div>
        </>
    )
}

export default FoodTable;
