import axios from "axios";
import React, { useEffect, useState } from "react";
import { useMediaQuery } from "react-responsive";
import './caloriesOverview.css'

const CaloriesOverview = ({date, profile}) => {
    const isTabletOrMobile = useMediaQuery({ query : '(max-width: 900px' })

    const [burnedCalories, setBurnedCalories] = useState(1000)
    const [takenCalories, setTakenCalories] = useState(1500)
    const calorieDeficit = takenCalories - burnedCalories
    return(
        <>
            <div className={isTabletOrMobile ? ("CaloriesOverview__Container__Mobile") : ("CaloriesOverview__Container__Desktop")}>
                <p>Konsumierte Kalorien: {takenCalories} kcal</p>
                <p>Verbrannte Kalorien: {burnedCalories} kcal</p>
                <p>Kaloriendefizit: {calorieDeficit} kcal</p>
            </div>
        </>
    )
}

export default CaloriesOverview;
