import {createAction, createReducer, createAsyncThunk} from "@reduxjs/toolkit";
import axios from "../axiosURL.js";

export const loadDays = createAsyncThunk("fitness/loadDays", async (date, profileId) => {
    const res = await axios.get(`/fitness/days/${profileId}/${date}`, {withCredentials: true})
    return res.data
});

export const updateDay = createAction("fitness/updateDay")
export const createDay = createAction("fitness/createDay")

const initialState = {
    day: []
};

const dayReducer = createReducer(initialState, (builder) => {
    builder.addCase(loadDays.fulfilled, (state, action) => {
        return{
            ...state,
            day: action.payload
        }
    })
    .addCase(createDay, (state, action) => {
        const dayCopy = [...state.day]
        const newDay = {
            date: action.payload.date, 
            food: [
                action.payload.foodId,
                action.payload.amount
                ],
            exercise: [
                action.payload.exerciseId,
                action.payload.timeInMinutes
            ],
            profileId: action.payload.profileId 
        }
        dayCopy.push(newDay)
        return {
            ...state,
            day: dayCopy
        }
    })
    .addCase(updateDay, (state, action) => {
        const exerciseIndex = state.exercise.findIndex(t => t._id === action.payload.exerciseId)
        const exerciseCopy = {...state.exercise[exerciseIndex]}
        const { exerciseId, ...rest } = action.payload;
        Object.assign(exerciseCopy, rest);
        const exerciseArrayCopy = [...state.exercise]
        exerciseArrayCopy[exerciseIndex] = exerciseCopy
        return{
            ...state,
            exercise: exerciseArrayCopy 
        }
    })
});

export default dayReducer;
