import React, { useState } from "react";
import "./food.css";

const Food = () => {

    const food = [
      {"name": "Apple", "baseAmount": 1, "energy": 52, "fat": 0.2, "carbohydrates": 14, "protein": 0.3, "salt": 0, "fiber": 2.4, "drink": false},
      {"name": "Potatoes", "baseAmount": 100, "energy": 77, "fat": 0.1, "carbohydrates": 17, "protein": 2, "salt": 0, "fiber": 2.2, "drink": false},
      {"name": "Rice", "baseAmount": 100, "energy": 130, "fat": 0.3, "carbohydrates": 28, "protein": 2.7, "salt": 0, "fiber": 0.4, "drink": false},
      {"name": "Salmon", "baseAmount": 100, "energy": 206, "fat": 13.4, "carbohydrates": 0, "protein": 22, "salt": 0.05, "fiber": 0, "drink": false},
      {"name": "Chicken", "baseAmount": 100, "energy": 165, "fat": 9.3, "carbohydrates": 0, "protein": 19, "salt": 0.1, "fiber": 0, "drink": false}
    ]

    const newFood = {"name": "", "baseAmount": null, "energy": null, "fat": null, "carbohydrates": null, "protein": null, "salt": null, "fiber": null, "drink": false}

    const initialSelectedFood = food[0];

    const [selectedFood, setSelectedFood] = useState(initialSelectedFood);

    const handleFoodClick = (index) => {
      // Set the selected food when a row is clicked
      setSelectedFood(food[index]);
    };

    const handleAttributeChange = (attribute, value) => {
      // Update the selected food's attribute when an input field is changed
      setSelectedFood((prevSelectedFood) => ({
        ...prevSelectedFood,
        [attribute]: value
      }));
    };

    const handleNewFood = () => {
        setSelectedFood(newFood)
    } 


    return (
      <div onClick={handleNewFood} className="Food__Container">
        <button>Neues Essen hinzuf√ºgen</button>
        <div className="Food__Table__Container">
          {food.map((item, index) => (
            <div key={index} onClick={() => handleFoodClick(index)} className={`Food__TableRow ${selectedFood.name===item.name ? "selected" : ""}`}>
              {item.name}
            </div>
          ))}
        </div>
        <div className="Food__SelectedFood__Container">
          <div className="Food__SelectedFood__Attributes">
            {Object.keys(selectedFood).map((attribute, index) => (
              <div key={index}>
                <strong>{attribute}: </strong>
                <input
                  type="text"
                  value={selectedFood[attribute]}
                  onChange={(e) => handleAttributeChange(attribute, e.target.value)}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    );
};

export default Food;
