import { useMediaQuery } from 'react-responsive'
import './foodTable.css'
import React from 'react'

const FoodTable = () => {
    const isTabletOrMobile = useMediaQuery({query: '(max-width:1224px)'})    

    const foodArray = [
        {"name": "Reis mit Gemüse", "amount": 200},
        {"name": "Chicken", "amount": 200}
    ]
    return(
        <>
            {isTabletOrMobile ? (
                <div className='FoodTable__Container__Mobile'>
                    <table>
                        <thead>
                            <tr>
                                <th style={{width: '60%'}}>Name</th>
                                <th style={{width: '40%'}}>Menge</th>
                            </tr>
                        </thead>
                        <tbody>
                            {foodArray.map((item, index) => (
                                <tr key={index}>
                                    <td style={{width: '60%'}}>{item.name}</td>
                                    <td style={{width: '40%'}}>{item.amount}g</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>               
                </div>
            ) : (
                <div>
                    Food
                </div>
            )}
        </>
    )
}

export default FoodTable;
